new Vue({el:"#vue",props:{items:{type:Array,required:!1,default:()=>[]}},data:{message:"hello Vue!",textSearch:"",result:[],show:!1,count:[]},watch:{textSearch:()=>{console.log()}},methods:{getCounter(){axios.get("https://gist.githubusercontent.com/gorborukov/0722a93c35dfba96337b/raw/435b297ac6d90d13a68935e1ec7a69a225969e58/russia").then(t=>{this.count=t.data.map(t=>t.city)})},Change(){0!=this.textSearch.length?(this.show=!0,this.filterResults(),this.getCounter()):this.show=!1},filterResults(){this.result=this.count.filter(t=>t.toLowerCase().indexOf(this.textSearch.toLowerCase())>-1)},setResults(t){this.textSearch=t,this.show=!1}}});